mapper.update:
UPDATE board
SET b_title = #{bTitle}
	,b_cont = #{bCont}
	,mod_id = #{modId}
	,mod_dt = SYSDATE
WHERE b_num = #{bNum}

UPDATE board
   SET bcount = bcount+1
WHERE b_num = #{bNum}

mapper.select:
SELECT count(*) as cnt
  FROM board_code
 WHERE b_num = #{bNum}
   AND user_id = #{userId}

mapper.insert:
INSERT INTO board_code (
			  b_seq,
		      b_num,
			  user_id
) VALUES (
			  BC_B_SEQ.nextval,
			  #{bNum},
			  #{userId}
)

mapper.update:
UPDATE BOARD	
   SET B_HIT = B_HIT + 1
 WHERE B_NUM = #{bNum}
