mapper.update:
UPDATE gallog
   SET g_title = #{gTitle}
      ,g_cont = #{gCont}
      ,mod_id = #{modId}
      ,mod_dt = sysdate
 WHERE g_seq = #{gSeq}

mapper.delete:
DELETE FROM gallog
 WHERE g_seq = #{gSeq}
   AND g_pw = #{gPw}

mapper.select:
      	SELECT COUNT(*) AS no
      	  FROM gallog
		 WHERE g_seq = #{gSeq}
		   AND g_pw = #{gPw}
