mapper.select:
SELECT X.*,Y.* FROM( SELECT T.msg_seq AS msgSeq, T.USER_ID AS userId, T.MSG_RECV_ID AS msgRecvId, T.MSG_CONT AS msgCont, TO_CHAR(T.REG_DT,'YYYY-MM-DD HH24:MI:SS') AS regDt, T.MSG_READ_YN AS msgReadYn, T.MSG_SDEL_YN AS msgSdelYn, T.MSG_RDEL_YN AS msgRdelYn, T.rnum AS no FROM( SELECT hr.* ,ROW_NUMBER() OVER(ORDER BY MSG_READ_YN desc , REG_DT desc ) AS rnum FROM MSG hr WHERE msg_sdel_yn = 0 AND USER_ID = #{userId}
)T WHERE rnum BETWEEN (#{page_size} * (#{page_num}-1)+1) AND (#{page_size} * (#{page_num}-1)+#{page_size}) )X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM MSG hr WHERE msg_sdel_yn = 0 AND USER_ID = #{userId}
)Y
